import React, { useState } from 'react';
import { Star, StarFill } from 'react-bootstrap-icons';
import axios from 'axios';

export const Job = ({job}) => {

    const starredHandler = () => {
        const flip = job.starred;
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !flip})
            .then(res => console.log(res.data.job))
            .catch(err => console.log(err))
    }
    const selectHandler = (e) => {
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {status: e.target.value})
            .then(res => console.log(res.data.job))
            .catch(err => console.log(err))
    }

    return ( 
        <tr>
            <td>{job.company}</td>
            <td>{job.position}</td>
            <td>{job.link}</td>
            <td className='d-flex justify-content-center'>
                <select className="form-select form-select-sm" style={{width:'10em'}} defaultValue={job.status} onChange={selectHandler}>
                    <option value="applied">applied</option>
                    <option value="interview">interview</option>
                    <option value="rejected">rejected</option>
                </select>
            </td>
            <td><a onClick={starredHandler} style={{cursor:'pointer'}}>{job.starred ? <StarFill /> : <Star  />}</a></td>
            <td className='text-left'>{job.notes}</td>
        </tr>
    )
}
