import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { Search } from './Search'
import { Job } from './Job'
import axios from 'axios'

export const JobsContainer = () => {
    const [jobs, setJobs] = useState([])
    const [viewJobs, setResults] = useState([])
    const [searchCategory, setCategory] = useState('company')

    useEffect(()=> {
        axios.get('http://localhost:8000/api/jobs')
            .then(res => {
                setJobs(res.data.jobs);
            })
            .catch(err => console.log(err))
    },[])
    useEffect(()=>{
        setResults(jobs)
    },[jobs])

    const deleteHandler = (id) => {// some form of delete confirmation (ie. a popup with a confirm delete button)
        axios.delete(`http://localhost:8000/api/jobs/${id}`)
            .then(res => {
                results = jobs.filter(job=>job._id !== id)
                setJobs(results);
            })
            .catch(err=> console.log(err))
    }
    const searchHandler = (e) => {
        const {value} = e.target;
        value.length > 0 ? setResults(jobs.filter(job=>~job[searchCategory].toLowerCase().indexOf(value.toLowerCase()))) : setResults(jobs)
    }
    return (
        <div className='mx-5'>
            <div className='d-flex gap-5'>
                <h1 className='my-0'>Jobs Container</h1>
            </div>
            <Search searchHandler={searchHandler} categoryHandler={setCategory}/>
            <table className="table table-striped text-center">
                <thead>
                    <tr>
                        <th scope="col">Position</th>
                        <th scope="col">Company</th>
                        <th scope="col">Posting</th>
                        <th scope="col">Status</th>
                        <th scope="col">Notes</th>
                        <th scope="col">Favorite</th>
                        <th scope="col">Delete</th>
                    </tr>
                </thead>
                <tbody className='border'>
                    {viewJobs?.map((job)=>{
                        return <Job key={job._id} job={job} deleteHandler={deleteHandler}/>
                    })}
                </tbody>
            </table>
        </div>
    )
}
