import React, { useState } from 'react';
import { Star, StarFill, Trash3Fill, JournalText, Link45deg } from 'react-bootstrap-icons';
import axios from 'axios';
import { NoteModal } from './Modal';

export const Job = ({job, deleteHandler, selectHandler}) => {
    const [starred,setStarred] = useState(job.starred);
    const [modal, seeModal] = useState(false);
    const starredHandler = () => {
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !starred})
            .then(res => setStarred(res.data.job.starred))
            .catch(err => console.log(err))
    }
    return (
        <>
            <tr>
                <td>{job.position}</td>
                <td>{job.company}</td>
                <td>
                    <select 
                        className="form-select form-select-sm" 
                        defaultValue={job.status} 
                        onChange={(e)=>selectHandler(e,job._id)}>
                        <option value="applied">applied</option>
                        <option value="interview">interview</option>
                        <option value="rejected">rejected</option>
                    </select>
                </td>
                <td>
                    <a 
                        onClick={()=>seeModal(true)} 
                        style={{cursor:'pointer'}}>
                        <JournalText />
                    </a>
                </td>
                <td>
                    <a 
                        href={job.link} 
                        target='_blank'>
                        <Link45deg />
                    </a>
                </td>
                <td>
                    <a 
                        onClick={starredHandler} 
                        style={{cursor:'pointer'}}>
                        {starred ? <StarFill color='gold'/> : <Star />}
                    </a>
                </td>
                <td>
                    <a 
                        onClick={() => deleteHandler(job._id)} 
                        style={{cursor:'pointer'}}>
                        <Trash3Fill color='grey'/>
                    </a>
                </td>
            </tr>
            <NoteModal show={modal} onHide={()=>seeModal(false)} job={job} />
        </> 
    )
}
