import React, { useState } from 'react';
import { Star, StarFill, Trash3Fill, JournalText, Link45deg } from 'react-bootstrap-icons';
import axios from 'axios';
import { NoteModal } from './Modal';
import { DeleteModal } from './DeleteModal';

export const Job = ({job, deleteHandler, selectHandler}) => {
    const [starred,setStarred] = useState(job.starred);
    const [notesModal, seeNotes] = useState(false);
    const [confirmModal, seeConfirmModal] = useState(false);
    const starredHandler = () => {
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !starred})
            .then(res => setStarred(res.data.job.starred))
            .catch(err => console.log(err))
    }
    return (
        <>
            <tr className='align-center'>
                <td>{job.position}</td>
                <td>{job.company}</td>
                <td>
                    <select 
                        className="form-select form-select-sm" 
                        defaultValue={job.status} 
                        onChange={(e)=>selectHandler(e,job._id)}>
                        <option value="applied">applied</option>
                        <option value="interview">interview</option>
                        <option value="rejected">rejected</option>
                    </select>
                </td>
                <td>
                    <a 
                        onClick={()=>seeNotes(true)} 
                        style={{cursor:'pointer'}}>
                        <JournalText />
                    </a>
                </td>
                <td>
                    <a 
                        href={job.link} 
                        target='_blank'>
                        <Link45deg />
                    </a>
                </td>
                <td>
                    <a 
                        onClick={starredHandler} 
                        style={{cursor:'pointer'}}>
                        {starred ? <StarFill color='gold'/> : <Star />}
                    </a>
                </td>
                <td>
                    <a 
                        onClick={() => seeConfirmModal(true)} 
                        style={{cursor:'pointer'}}>
                        <Trash3Fill color='grey'/>
                    </a>
                </td>
            </tr>
            <NoteModal show={notesModal} onHide={()=>seeNotes(false)} job={job} />
            <DeleteModal show={confirmModal} onHide={()=>seeConfirmModal(false)} jobTitle={`${job.position} at ${job.company}`} jobId={job._id} deleteHandler={deleteHandler} />
        </> 
    )
}
