import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';

export const NotesModal = ({show,onHide,job}) => {
    const [edit, setEdit] = useState(false)
    const [state,setState] = useState({
        notes: job.notes
    })
    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, [name]: value})
    }
    const editNotes = () => {
        setEdit(false);
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {notes: state.notes})
            .then(res => {setState({notes: res.data.job.notes})})
            .catch(err => console.log(err))
    }
    return (
            <Modal
                show={show}
                onHide={onHide}
                size="lg"
                scrollable={true}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {job.position} at {job.company}
                    </Modal.Title>
                </Modal.Header>
                {edit ? 
                (
                <div className="mb-3 px-3">
                    <label htmlFor="notes" className="form-label mt-1">Editing Notes</label>
                    <textarea onChange={changeHandler} value={state.notes} name="notes" className="form-control" id="notes" maxLength='250' rows='12'/>
                    <small>{state.notes.length}/250</small>
                </div>)
                : 
                (<Modal.Body>
                    {state.notes.length > 0
                        ? ( <>
                                <h6 className='mb-3 pb-1'>Notes:</h6>
                                <p className='ms-2 text-break' style={{whiteSpace: 'pre-line'}}>{state.notes}</p>
                            </>
                            )
                        : <p className='text-muted text-center'>No notes to display...</p>
                    }
                </Modal.Body>)
                }   
                <Modal.Footer>
                    {edit ? <Button onClick={editNotes}>Save</Button> : <Button onClick={()=>setEdit(true)}>Edit</Button>}
                    <Button onClick={onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
}
