import React, { useState } from 'react'
import { LogIn } from './Login';
import { Register } from './Register';

export const AuthContainer = () => {
    const [visible, setVisible] = useState(false)
    const [loginForm, setFormType] = useState(true)
    const [formData, setFormData] = useState({})

    const submitHandler = (e, data) => {
        e.preventDefault();
        setFormData(data.formData)
        sendData(data.type)
    }

    const sendData = (type) => {
        const url = '';
        switch (type) {
            case 'LOGIN':
                // post info to /login route loging user in
                break;

            case 'REGISTER':
                // post info to /register route creating new user, and logging them in
                break;

            default:
                // redirect back to login/signup page
                break;
        }
    }

    return (
        <div className='container'>
            <h6 className='text-center'>Logging In or Signing Up?</h6>
            <div className="d-flex my-switch align-items-center gap-2 justify-content-center">
                <div className="form-text m-0">Logging In</div>
                <div className="form-check form-switch form-check-inline m-0 p-0 align-items-center d-flex">
                    <input id="revenue" className="form-check-input m-0" type="checkbox" onChange={() => setFormType(!loginForm)}/>
                </div>
                <div className="form-text m-0">Signing Up</div>
            </div>
            {loginForm ? (<LogIn visible={visible} setVisible={setVisible} submitHandler={submitHandler} />) : (<Register visible={visible} setVisible={setVisible} submitHandler={submitHandler} />)}
        </div>
    )
}
