import React, { useState } from 'react';
import { Star, StarFill, Trash3Fill, Eyeglasses } from 'react-bootstrap-icons';
import axios from 'axios';
import { NotesModal } from './Modal';

export const Job = ({job, deleteHandler}) => {
    const [starred,setStarred] = useState(job.starred);
    const [modal, seeModal] = useState(false);

    const starredHandler = () => {
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !starred})
            .then(res => setStarred(res.data.job.starred))
            .catch(err => console.log(err))
    }
    const selectHandler = (e) => {
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {status: e.target.value})
            .then(res => console.log(res.data.job))
            .catch(err => console.log(err))
    }

    return (
        <>
            <tr>
                <td>{job.position}</td>
                <td>{job.company}</td>
                <td><a href={job.link} target='_blank'>Link</a></td>
                <td>
                    <select className="form-select form-select-sm" defaultValue={job.status} onChange={selectHandler}>
                        <option value="applied">applied</option>
                        <option value="interview">interview</option>
                        <option value="rejected">rejected</option>
                    </select>
                </td>
                <td><a onClick={()=>seeModal(true)} style={{cursor:'pointer'}}><Eyeglasses /></a></td>
                <td><a onClick={starredHandler} style={{cursor:'pointer'}}>{starred ? <StarFill /> : <Star  />}</a></td>
                <td><a onClick={() => deleteHandler(job._id)} style={{cursor:'pointer'}}><Trash3Fill /></a></td>
            </tr>
            <NotesModal show={modal} onHide={()=>seeModal(false)} job={job} />
        </> 
    )
}
