import React, { useState, useContext } from 'react';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/auth.context';

export const NoteModal = ({show,onHide,job}) => {
    const {user} = useContext(AuthContext)
    const [edit, setEdit] = useState(false)
    const [state,setState] = useState({
        notes: job.notes,
        link: job.link
    })
    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, [name]: value})
    }
    const editNotes = () => {
        setEdit(false);
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {notes: state.notes}, {
            headers: {
                auth_token: `Bearer ${user.token}`
            }
        }).then(res => {
                if(!res.data.err){
                    setState({notes: res.data.job.notes})
                } else {
                    console.log(res.data.err)
                }})
    }
    return (
            <Modal
                show={show}
                onHide={onHide}
                size="lg"
                scrollable={true}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton className='d-flex flex-column align-items-start'>
                    <Modal.Title id="contained-modal-title-vcenter" >
                        {job.position} at {job.company}
                    </Modal.Title>
                    {edit ? 
                        (
                        <div className="px-3 py-3">
                            <input onChange={changeHandler} value={state.link} name='link' className="form-control"/>
                        </div>)
                        : 
                        job.link 
                        ?(<a href={job.link}>
                            Link to job posting
                        </a>)
                        :(<small className='text-muted mb-0'>
                            Click edit to add a link
                        </small>)
                    }
                </Modal.Header>
                {edit ? 
                (
                <div className="px-3 py-3">
                    <label htmlFor="notes" className="form-label mt-1">Editing Notes</label>
                    <textarea onChange={changeHandler} value={state.notes} name="notes" className="form-control" id="notes" maxLength='250' rows='12'/>
                    <small>{state.notes.length}/250</small>
                </div>)
                : 
                (<Modal.Body>
                    {state.notes.length > 0
                        ? ( <>
                                <h6 className='pb-4'>Notes:</h6>
                                <p className='ms-2 text-break' style={{whiteSpace: 'pre-line'}}>{state.notes}</p>
                            </>
                            )
                        : <p className='text-muted text-center'>No notes to display...</p>
                    }
                </Modal.Body>)
                }   
                <Modal.Footer>
                    {edit ? <Button onClick={editNotes}>Save</Button> : <Button onClick={()=>setEdit(true)}>Edit</Button>}
                    <Button onClick={onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
}
