import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { ValidatorError } from './ValidatorError';

export const JobForm = () => {
    const navigate = useNavigate();
    const [state,setState] = useState({
        formData: {
            company: {value:'', error: false},
            position: {value:'', error: false},
            link: {value:'', error: false},
            notes: {value:'', error: false},
            status: 'applied',
            starred: false
        }
    })

    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, 
            formData: {...state.formData, 
                [name]: {value: value, error: formValidation(name)}
            }
        })
    }

    const submitHandler = (e) => {
        e.preventDefault();
        const {company,position,link,notes,status,starred} = state.formData;
        const fields = [company,position,link,notes]
        const body = {starred: starred, status: status};
        fields.forEach((field)=>{
            if (field.error) body.get(field,field.value)
        })
        console.log(body)
        // axios.post('http://localhost:8000/api/jobs/', state.formData)
        //     .then((res)=>{
        //         if(res.data.err){
        //             console.log(res.data.err)
        //         } else {
        //             navigate('/');
        //         }
        //     })
        //     .catch((err) => {console.log(err.message)})
    }

    const formValidation = (name) => {
        const linkRegex = /((([A-Za-z]{3,9}:(?:\/\/)?)(?:[-;:&=\+\$,\w]+@)?[A-Za-z0-9.-]+|(?:www.|[-;:&=\+\$,\w]+@)[A-Za-z0-9.-]+)((?:\/[\+~%\/.\w-_]*)?\??(?:[-\+=&;%@.\w_]*)#?(?:[\w]*))?)/;
        switch (name) {
            case 'company':
                if (state.formData.company.value.length < 1) return true;
                break;
            case 'position':
                if (state.formData.position.value.length < 1) return true;
                break;
            case 'link':
                if (state.formData.link.value.length < 3) return true;
                if (!linkRegex.test(state.formData.link.value)) return true;
                break;
            case 'notes':
                if (state.formData.notes.value.length > 250) return true;
                break;
            default:
                break;
        }
        return false
    }

    return (
        <div className='mx-5 px-5'>
            <form onSubmit={(e) => submitHandler(e,state)}>
                <div className="mb-3">
                    <label 
                        htmlFor="position" 
                        className="form-label">Job Title</label>
                    <input
                        onChange={changeHandler} 
                        value={state.formData.position.value} 
                        name="position" type="text" 
                        className="form-control" 
                        id="position"/>
                    {state.formData.position.error && (<ValidatorError message='Please Enter a Job Title'/>)}
                </div>
                <div className="mb-3">
                    <label 
                        htmlFor="company" 
                        className="form-label">Company Name</label>
                    <input 
                        onChange={changeHandler} 
                        value={state.formData.company.value} 
                        name="company" 
                        type="text" 
                        className="form-control" 
                        id="company"/>
                    {state.formData.company.error && (<ValidatorError message='Please Enter a Company Name'/>)}
                </div>
                <div className="mb-3">
                    <label 
                        htmlFor="link" 
                        className="form-label">Link to Job Posting <small className='text-muted'>(Copy/Paste for best results)</small></label>
                    <input 
                        onChange={changeHandler} 
                        value={state.formData.link.value} 
                        name="link" 
                        type='text' 
                        className="form-control" 
                        id="link"/>
                    {state.formData.link.error && (<ValidatorError message='Please Use a Valid Link'/>)}
                </div>
                <div className="mb-3">
                    <label 
                        htmlFor="notes" 
                        className="form-label">Additional Notes</label>
                    <textarea 
                        onChange={changeHandler} 
                        value={state.formData.notes.value} 
                        name="notes" 
                        className="form-control" 
                        id="notes" 
                        maxLength='250' 
                        rows='3'/>
                    <small>{state.formData.notes.value.length}/250</small>
                    {state.formData.notes.error && (<ValidatorError message='Notes Must Be Less Than 250 Characters'/>)}
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
