import React, { useState } from 'react'

export const JobForm = () => {
    const [state,setState] = useState({
        formData: {
            company: '',
            position: '',
            status: 'applied',
            link: '',
            starred: false
        }
    })

    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, formData: {...state.formData, [name]: value}})
    }


    const submitHandler = (e) => {
        e.preventDefault();
    }

    return (
        <div className='mx-5'>
            <form onSubmit={(e) => submitHandler(e,state)}>
                <div className="mb-3">
                    <label htmlFor="company" className="form-label">Company Name</label>
                    <input onChange={changeHandler} value={state.company} name="company" type="text" className="form-control" id="company"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="position" className="form-label">Position Title</label>
                    <input onChange={changeHandler} value={state.position} name="position" type="text" className="form-control" id="position"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="link" className="form-label">Link to Job Posting</label>
                    <input onChange={changeHandler} value={state.link} name="link" type='text' className="form-control" id="link"/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
