import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { Star, StarFill } from 'react-bootstrap-icons';
import axios from 'axios';

export const Job = ({job}) => {

    const starredHandler = () => {
        const flip = job.starred;
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !flip})
            .then(res => console.log(res.data.job))
            .catch(err => console.log(err))
    }

    return ( 
        <tr>
            <td>{job.company}</td>
            <td>{job.position}</td>
            <td>{job.link}</td>
            <td>
                <select class="form-select form-select-sm">
                    <option value="">Open this select menu</option>
                    <option value="applied">One</option>
                    <option value="interview">Two</option>
                    <option value="offer">Three</option>
                </select>
            </td>
            <td><a onClick={starredHandler} style={{cursor:'pointer'}}>{job.starred ? <StarFill /> : <Star  />}</a></td>
            <td>{job.notes}</td>
        </tr>
    )
}
