import React from 'react';
import { Link } from 'react-router-dom';
import { Star, StarFill } from 'react-bootstrap-icons';
import axios from 'axios';

export const Job = ({job}) => {
    const [thisJsob, setJob] = useState(job)
    const starredHandler = () => {
        const flip = job.starred;
        axios.put(`http://localhost:8000/api/jobs/${job._id}`, {starred: !flip})
            .then(res => console.log(res.data))
            .catch(err => console.log(err))
    }

    return ( 
        <tr>
            <td>{thisJob.company}</td>
            <td>{thisJob.position}</td>
            <td>{thisJob.link}</td>
            <td>{thisJob.status}</td>
            <td><a onClick={starredHandler} style={{cursor:'pointer'}}>{thisJob.starred ? <StarFill /> : <Star />}</a></td>
            <td>{thisJob.notes}</td>
        </tr>
    )
}
