import React, { useEffect, useState } from 'react'
import { Search } from './Search'
import { Job } from './Job'
import { Loading } from './Loading'
import axios from 'axios'

export const JobsContainer = () => {
    const [jobs, setJobs] = useState([])
    const [viewJobs, setResults] = useState([])
    const [searchCategory, setCategory] = useState('company')
    const [loading, setLoading] = useState(true)

    useEffect(()=> {
        axios.get('http://localhost:8000/api/jobs')
            .then(res => {
                setJobs(res.data.jobs);
            })
            .catch(err => console.log(err))
    },[])

    useEffect(()=>{
        const timer = setTimeout(() => {
            setLoading(false)
            setResults(jobs)
        }, 400);
        return () => clearTimeout(timer);
    },[jobs])

    const deleteHandler = (id) => {
        // some form of delete confirmation (ie. an overlay with a confirm delete button)
        axios.delete(`http://localhost:8000/api/jobs/${id}`)
            .then(res => {
                setJobs(jobs.filter(job=>job._id !== id));
            })
            .catch(err=> console.log(err))
    }
    const searchHandler = (e) => {
        const {value} = e.target;
        value.length > 0 ? 
            setResults(jobs.filter(job=>~job[searchCategory]
                                            .toLowerCase()
                                            .indexOf(value.toLowerCase()))) 
            : setResults(jobs)
    }
    const selectHandler = (e,id) => {
        axios.put(`http://localhost:8000/api/jobs/${id}`, {status: e.target.value})
            .then(res => {
                const newState = jobs.map(obj => {
                    if(obj._id == id){
                        return res.data.job
                    }
                    return obj
                })
                setJobs(newState)
            })
            .catch(err => console.log(err))
    }
    return (
        <div className='mx-5'>
            <div className='d-flex gap-5'>
                <h1 className='my-0'>Dashboard</h1>
            </div>
            <Search searchHandler={searchHandler} categoryHandler={setCategory}/>
            {loading ?
                <Loading />
            :(<table className="table table-striped text-center">
                <thead>
                    <tr>
                        <th scope="col">Position</th>
                        <th scope="col">Company</th>
                        <th scope="col">Status</th>
                        <th scope="col">Notes</th>
                        <th scope="col">Job Posting</th>
                        <th scope="col">Favorite</th>
                        <th scope="col">Delete</th>
                    </tr>
                </thead>
                <tbody className='border'>
                    {viewJobs?.map((job)=>{
                        return <Job
                                    key={job._id} 
                                    job={job} 
                                    deleteHandler={deleteHandler} 
                                    selectHandler={selectHandler} />
                    })}
                </tbody>
            </table>)}
        </div>
    )
}
