import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';


export const NotesModal = ({show,onHide,job,editNotes}) => {
    const [edit, setEdit] = useState(false)
    const [state,setState] = useState({
        notes: job.notes
    })

    const submit = () => {
        editNotes(job._id,state.notes);
        setEdit(false);
    }

    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, [name]: value})
    }
    return (
            <Modal
                show={show}
                onHide={onHide}
                size="lg"
                scrollable={true}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {job.position} at {job.company}
                    </Modal.Title>
                </Modal.Header>
                {edit ? 
                (
                <div className="mb-3 px-3">
                    <label htmlFor="notes" className="form-label">Editing Notes</label>
                    <textarea onChange={changeHandler} value={state.notes} name="notes" className="form-control" id="notes" maxLength='250' rows='12'/>
                    <small>{state.notes.length}/250</small>
                </div>)
                : 
                (<Modal.Body>
                    {job.notes.length > 0
                        ? ( <>
                                <h4>Notes:</h4>
                                <p className='text-break' style={{whiteSpace: 'pre-line'}}>{job.notes}</p>
                            </>
                            )
                        : <p className='text-muted text-center'>No notes to display...</p>
                    }
                </Modal.Body>)
                }   
                <Modal.Footer>
                    {edit ? <Button onClick={submit}>Save</Button> : <Button onClick={()=>setEdit(true)}>Edit</Button>}
                    <Button onClick={onHide}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
}
