import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

export const JobForm = () => {
    const navigate = useNavigate();
    const [state,setState] = useState({
        formData: {
            company: '',
            position: '',
            status: 'applied',
            link: '',
            starred: false,
            notes: ''
        },
        formValid: false
    })

    const changeHandler = (e) => {
        const {name, value} = e.target;
        setState({...state, formData: {...state.formData, [name]: value}})
    }

    const submitHandler = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/jobs/', state.formData)
            .then((res)=>{
                if(res.data.err){
                    console.log(res.data.err)
                } else {
                    navigate('/');
                }
            })
            .catch((err) => {console.log(err.message)})
    }

    const formValidation = ({name,value}) => {
        const linkRegex = /((([A-Za-z]{3,9}:(?:\/\/)?)(?:[-;:&=\+\$,\w]+@)?[A-Za-z0-9.-]+|(?:www.|[-;:&=\+\$,\w]+@)[A-Za-z0-9.-]+)((?:\/[\+~%\/.\w-_]*)?\??(?:[-\+=&;%@.\w_]*)#?(?:[\w]*))?)/;
        switch (name) {
            case 'company':
                if (value.length < 1) return false;
                break;
            case 'position':
                if (value.length < 1) return false;
                break;
            case 'link':
                if (value.length < 3) return false;
                if (!value.match(regex)) return false;
                break;
            case 'link':
                if (value.length < 3) return false;
                if (!value.match(regex)) return false;
                break;
            default:
                break;
        }
    }

    return (
        <div className='mx-5'>
            <form onSubmit={(e) => submitHandler(e,state)}>
                <div className="mb-3">
                    <label htmlFor="company" className="form-label">Company Name</label>
                    <input onChange={changeHandler} value={state.company} name="company" type="text" className="form-control" id="company"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="position" className="form-label">Position Title</label>
                    <input onChange={changeHandler} value={state.position} name="position" type="text" className="form-control" id="position"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="link" className="form-label">Link to Job Posting</label>
                    <input onChange={changeHandler} value={state.link} name="link" type='text' className="form-control" id="link"/>
                </div>
                <div className="mb-3">
                    <label htmlFor="notes" className="form-label">Notes About the Job</label>
                    <textarea onChange={changeHandler} value={state.notes} name="notes" className="form-control" id="notes"/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
